import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  state = {
    currencies: [
          {
          name:'Bitcoin',
          abrev: 'btc',
          price: 0,
          visible: false,
          },
            {
          name: 'Ethereum',
          abrev: 'eth',
          price: 0,
          },
          {
          name: 'Litecoin',
          abrev: 'ltc',
          price: 0,
          },
          {
          name: 'US Dollar',
          abrev: 'usd',
          price: 0,
          }
        ],
    showBitcoin: true,

  }


  toggleBitcoinBar = () => {
    if(this.state.showBitcoin) {
        this.setState({
          showBitcoin: false,
        });
        console.log('hide it')
    } else {
      this.setState({
          showBitcoin: true,
        });
        console.log('show it')
    }

  }


  render() {
      console.log("render", this.state.currencies);
      return (
        <div className="App">

          <nav className="nav">
            <img src={logo} className="App-logo" alt="logo" />
            <a href="https://rjplaton.github.io/stellar-pairings/">XLM Chart</a>
          </nav>

<div className="wrapper">
  <div className="header"><h1>Stellar Lumens (XLM) Conversion</h1></div>

  <div className="sidebar box">
    <h3>
      <p>XLM Pairings</p>
    </h3>
        <div className="button" onClick="">
      Update Chart
    </div>  
    <p></p>
    <p><input type="checkbox" id="btc" onClick={this.visibleBitcoinBar} />BTC</p>
    <p><input type="checkbox" id="eth" onclick="" />ETH</p>
    <p><input type="checkbox" id="ltc" onclick="" />LTC</p>
    <p><input type="checkbox" id="usd" onclick="" />USD</p>

  </div>


<div className="content">
<div className="BarChart">
<h2>How many XLM per...</h2>

          {this.state.currencies.map(currency => (
            <div className="BarChart-row" id={ this.state.showBitcoin ? currency.abrev + "Row" : currency.abrev + "Row-hidden"}>
            <div className="BarChart-rowdesc">1 {currency.name} ({currency.abrev.toUpperCase()})</div>  
            <div className="BarChart-bar BarChart-number" id={currency.abrev + "Bar"} ><p><span>?</span></p></div>

    </div>
            ))}

</div>
  </div>



  <div className="footer">
    <a href="https://github.com/rjplaton/stellar-pairings">Github Repo</a>
    <p>
    <a href="http://rjplaton.com">Reuben Platon</a>
  </p>
    </div>
</div>


        </div>
      );
    }
  }

export default App;
