import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  state = { 
    currencies: [
          {
          name:'Bitcoin',
          abrev: 'btc',
          price: 0,
          visible: false,
          },
            {
          name: 'Ethereum',
          abrev: 'eth',
          price: 0,
          visible: false,
          },
          {
          name: 'Litecoin',
          abrev: 'ltc',
          price: 0,
          visible: false,
          },
          {
          name: 'US Dollar',
          abrev: 'usd',
          price: 0,
          visible: false,
          }
        ],

  }


  toggleBtcBar = () => {
    if(this.state.showBtc) {
        this.setState({
          showBtc: false,
        });
        console.log('hide it');
    } else {
      this.setState({
          showBtc: true,
        });
        console.log('show it');
    }

  }

    toggleEthBar = () => {
    if(this.state.showEth) {
        this.setState({
          showEth: false,
        });
        console.log('hide it')
    } else {
      this.setState({
          showEth: true,
        });
        console.log('show it')
    }

  }



  onCurrencyToggle = (id) => {
    if(this.state.currencies[id].visible === true) {
        this.state.currencies[id].visible = false;
        this.setState({
            currencies: this.state.currencies,
        });
        } else {
        this.state.currencies[id].visible = true;
        this.setState({
            currencies: this.state.currencies,
        });
      }

  }




  render() {
      console.log("state rendered with this data:", this.state.currencies);
      return (
        <div className="App">

          <nav className="nav">
            <img src={logo} className="App-logo" alt="logo" />
            <a href="https://rjplaton.github.io/stellar-pairings/">XLM Chart</a>
          </nav>

<div className="wrapper">
  <div className="header"><h1>Stellar Lumens (XLM) Conversion</h1></div>

  <div className="sidebar box">
    <h3>
      <p>XLM Pairings</p>
    </h3>
        <div className="button" onClick="">
      Update Chart
    </div>  
    <p></p>
    {
    this.state.currencies.map( (currency, id) => (
       <p><input type="checkbox" id={currency.abrev} onClick={() => this.onCurrencyToggle(id)} />{currency.abrev.toUpperCase()}</p>
      ))
    }
  </div>


<div className="content">
<div className="BarChart">
<h2>How many XLM per...</h2>

          {this.state.currencies.map(currency => (
            <div className="BarChart-row" id={ 


              currency.visible === true ? currency.abrev + "Row" : currency.abrev + "Row-hidden"}>
            <div className="BarChart-rowdesc">1 {currency.name} ({currency.abrev.toUpperCase()})</div>  
            <div className="BarChart-bar BarChart-number" id={currency.abrev + "Bar"} ><p><span>?</span></p></div>

    </div>
            ))}

</div>
  </div>



  <div className="footer">
    <a href="https://github.com/rjplaton/stellar-pairings">Github Repo</a>
    <p>
    <a href="http://rjplaton.com">Reuben Platon</a>
  </p>
    </div>
</div>


        </div>
      );
    }
  }

export default App;
